import React from 'react';
import { Link } from 'react-router-dom';
import {
  AcademicCapIcon,
  EnvelopeIcon,
  PhoneIcon,
  MapPinIcon,
  GlobeAltIcon
} from '@heroicons/react/24/outline';
import { useLanguage } from '../../contexts/LanguageContext';

const Footer = () => {
  const { t, language } = useLanguage();

  const footerLinks = {
    platform: [
      { name: t('colleges'), href: '/colleges' },
      { name: t('courses'), href: '/courses' },
      { name: t('comparison'), href: '/comparison' },
      { name: t('search'), href: '/search' }
    ],
    resources: [
      { name: t('cutoffs'), href: '/cutoffs' },
      { name: t('placements'), href: '/placements' },
      { name: language === 'ta' ? '‡Æé‡Æ™‡Øç‡Æ™‡Æü‡Æø ‡Æ™‡ÆØ‡Æ©‡Øç‡Æ™‡Æü‡ØÅ‡Æ§‡Øç‡Æ§‡ØÅ‡Æµ‡Æ§‡ØÅ' : 'How to Use', href: '#' },
      { name: language === 'ta' ? '‡ÆÖ‡Æü‡Æø‡Æï‡Øç‡Æï‡Æü‡Øç‡Æü‡ØÅ ‡Æï‡Øá‡Æ≥‡Øç‡Æµ‡Æø‡Æï‡Æ≥‡Øç' : 'FAQs', href: '#' }
    ],
    company: [
      { name: t('about'), href: '/about' },
      { name: t('contact'), href: '/contact' },
      { name: t('privacyPolicy'), href: '#' },
      { name: t('termsOfService'), href: '#' }
    ]
  };

  const socialLinks = [
    { name: 'Facebook', href: '#', icon: 'f' },
    { name: 'Twitter', href: '#', icon: 'ùïè' },
    { name: 'LinkedIn', href: '#', icon: 'in' },
    { name: 'Instagram', href: '#', icon: 'üì∑' }
  ];

  return (
    <footer className="bg-gray-900 text-white">
      <div className="container mx-auto px-4 py-12">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-8">
          {/* Brand */}
          <div className="lg:col-span-2">
            <div className="flex items-center space-x-3 mb-4">
              <div className="w-10 h-10 bg-primary-600 rounded-lg flex items-center justify-center">
                <AcademicCapIcon className="h-6 w-6 text-white" />
              </div>
              <div>
                <h3 className="text-xl font-bold">
                  {language === 'ta' ? '‡Æï‡Æ≤‡Øç‡Æ≤‡ØÇ‡Æ∞‡Æø ‡Æ§‡Øá‡Æü‡ØÅ‡Æ™‡Æµ‡Æ∞‡Øç' : 'College Finder'}
                </h3>
                <p className="text-sm text-gray-400">Tamil Nadu</p>
              </div>
            </div>
            <p className="text-gray-300 mb-6 max-w-md">
              {language === 'ta'
                ? '‡Æ§‡ÆÆ‡Æø‡Æ¥‡Øç‡Æ®‡Ææ‡Æü‡Øç‡Æü‡Æø‡Æ©‡Øç 12-‡ÆÜ‡ÆÆ‡Øç ‡Æµ‡Æï‡ØÅ‡Æ™‡Øç‡Æ™‡ØÅ ‡ÆÆ‡Ææ‡Æ£‡Æµ‡Æ∞‡Øç‡Æï‡Æ≥‡ØÅ‡Æï‡Øç‡Æï‡ØÅ ‡Æö‡Æ∞‡Æø‡ÆØ‡Ææ‡Æ© ‡Æ™‡Øä‡Æ±‡Æø‡ÆØ‡Æø‡ÆØ‡Æ≤‡Øç ‡Æï‡Æ≤‡Øç‡Æ≤‡ØÇ‡Æ∞‡Æø ‡ÆÆ‡Æ±‡Øç‡Æ±‡ØÅ‡ÆÆ‡Øç ‡Æ™‡Ææ‡Æü‡Æ®‡ØÜ‡Æ±‡Æø‡ÆØ‡Øà‡Æ§‡Øç ‡Æ§‡Øá‡Æ∞‡Øç‡Æ®‡Øç‡Æ§‡ØÜ‡Æü‡ØÅ‡Æ™‡Øç‡Æ™‡Æ§‡Æ±‡Øç‡Æï‡ØÅ ‡Æâ‡Æ§‡Æµ‡ØÅ‡ÆÆ‡Øç ‡Æµ‡Æ¥‡Æø‡Æï‡Ææ‡Æü‡Øç‡Æü‡Æø.'
                : 'Your guide to choosing the right engineering college and course for 12th standard students in Tamil Nadu.'
              }
            </p>

            {/* Contact Info */}
            <div className="space-y-3">
              <div className="flex items-center space-x-3">
                <EnvelopeIcon className="h-5 w-5 text-gray-400" />
                <a href="mailto:support@collegefinder.tn" className="text-gray-300 hover:text-white">
                  support@collegefinder.tn
                </a>
              </div>
              <div className="flex items-center space-x-3">
                <PhoneIcon className="h-5 w-5 text-gray-400" />
                <a href="tel:+919876543210" className="text-gray-300 hover:text-white">
                  +91 98765 43210
                </a>
              </div>
              <div className="flex items-center space-x-3">
                <MapPinIcon className="h-5 w-5 text-gray-400" />
                <span className="text-gray-300">
                  {language === 'ta' ? '‡Æö‡ØÜ‡Æ©‡Øç‡Æ©‡Øà, ‡Æ§‡ÆÆ‡Æø‡Æ¥‡Øç‡Æ®‡Ææ‡Æü‡ØÅ' : 'Chennai, Tamil Nadu'}
                </span>
              </div>
            </div>
          </div>

          {/* Links */}
          <div>
            <h4 className="text-lg font-semibold mb-4">
              {language === 'ta' ? '‡Æ§‡Æ≥‡ÆÆ‡Øç' : 'Platform'}
            </h4>
            <ul className="space-y-3">
              {footerLinks.platform.map((link) => (
                <li key={link.name}>
                  <Link
                    to={link.href}
                    className="text-gray-300 hover:text-white transition-colors"
                  >
                    {link.name}
                  </Link>
                </li>
              ))}
            </ul>
          </div>

          <div>
            <h4 className="text-lg font-semibold mb-4">
              {language === 'ta' ? '‡Æµ‡Æ≥‡Æô‡Øç‡Æï‡Æ≥‡Øç' : 'Resources'}
            </h4>
            <ul className="space-y-3">
              {footerLinks.resources.map((link) => (
                <li key={link.name}>
